var driver = new webdriver.Builder().build();
driver.get('http://www.google.com').then(function() {
    driver.findElement(webdriver.By.name('q')).then(function(searchBox){
     searchBox.sendKeys('webdriver').then(function() {
       driver.findElement(webdriver.By.name('btnG')).then(function(submitButton) {
         submitButton.click().then(function() {
           driver.getTitle().then(function(title) {
            assertEquals('webdriver - Google Search', title);
          });
        });
      });
    });
  });
});
